tinymce.PluginManager.add('sb', function(editor, url) {
    editor.addButton('sb', {
        text: false,
        icon: 'sb',
		title: 'Scratch Projekt',
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Scratch Projekt einfügen',
                body: [
                    {type: 'textbox', name: 'id', label: 'Projektnummer'}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent('<a href="javascript: showsbpopup('+e.data.id+');"><img src="img/sbplayer.php?id='+e.data.id+'" height="387" width="482" style="max-width: 482px; max-height: 387px; min-width: 482px; max-width: 387px; cursor: pointer;" /></a>');
                }
            });
        }
    });
});