tinymce.PluginManager.add('sb', function(editor, url) {
    editor.addButton('sb', {
        text: false,
        icon: 'sb',
		title: 'Scratch Projekt',
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Scratch Projekt einfügen',
                body: [
                    {type: 'textbox', name: 'id', label: 'Projektnummer'}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent('<iframe allowtransparency="true" width="485" height="402" src="http://scratch.mit.edu/projects/embed/' + e.data.id + '/?autostart=false" frameborder="0" allowfullscreen></iframe>');
                }
            });
        }
    });
});